import "./landingPage.css";
import { Link, Routes, Route, useNavigate } from "react-router-dom";
import React from "react";

import landingMainImage from "./images/landing-page-main-img.png";
import landingStarImage from "./images/landing-page-star-img.png";
import Footer from "./footer";
import styled, { keyframes } from "styled-components";

const topToBottom = keyframes`
  0% {
    opacity: 0;
  }
  6% {
    opacity: 0;
    transform: translateY(-30px);
  }
  10% {
    opacity: 1;
    transform: translateY(0px);
  }
  25% {
    opacity: 1;
    transform: translateY(0px);
  }
  29% {
    opacity: 0;
    transform: translateY(30px);
  }
  80% {
    opacity: 0;
  }
  100% {
    opacity: 0;
  }
`;
const SliderDiv = styled.div`
  display: inline;
  & span {
    animation: ${topToBottom} 10s linear infinite 0s;
    opacity: 0;
    position: absolute;

    :nth-child(2) {
      animation-delay: 2.5s;
    }

    :nth-child(3) {
      animation-delay: 5s;
    }

    :nth-child(4) {
      animation-delay: 7.5s;
    }
  }
`;
function LandingPage() {
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    console.log("onnnnnn");
    event.preventDefault();
    navigate("/formPage");
  };
  return (
    <>
      <SliderDiv>
        {items.map((item) => (
          <span key={item} css={{ color }}>
            {item}
          </span>
        ))}
      </SliderDiv>
      <img src={landingMainImage} className="landing-main-img" alt="mainimg" />
      <div className="welcome-div">
        <p className="welcome-paragraph">
          WELCOME TO
          <br />
          <br />
          YOUR JOURNEY THROUGH
          <br />
          <br />
          CUSTOMER EXPERIENCE
        </p>
        <button onClick={handleSubmit} className="landing-button">
          Be part of the conversation
        </button>
        <h4 className="learn-more">LEARN MORE</h4>
      </div>
      <div
        className="landing-star-img-div"
        style={{ backgroundImage: `url(${landingStarImage})` }}
      >
        <Footer />
      </div>
    </>
  );
}

export default LandingPage;
